import React, { useRef, useEffect } from 'react';
import mapboxgl from 'mapbox-gl';
import 'mapbox-gl/dist/mapbox-gl.css';

export default function Map() {
  const mapContainer = useRef(null);

  useEffect(() => {
    const token = process.env.REACT_APP_MAPBOX_TOKEN;
    if (!token) {
      // Inform developers that the token is missing; the map will not initialize.
      // This allows the app to build/run without failing when no token is provided.
      // In production provide `REACT_APP_MAPBOX_TOKEN` in your environment.
      // eslint-disable-next-line no-console
      console.warn('REACT_APP_MAPBOX_TOKEN is not set. Mapbox map will not initialize.');
      return;
    }

    mapboxgl.accessToken = token;

    const map = new mapboxgl.Map({
      container: mapContainer.current,
      style: 'mapbox://styles/mapbox/streets-v11',
      center: [-74.006, 40.7128], // Default to New York City
      zoom: 12,
    });

    // Add navigation control (zoom in/out)
    map.addControl(new mapboxgl.NavigationControl());

    // Add a default marker at center
    new mapboxgl.Marker().setLngLat([-74.006, 40.7128]).addTo(map);

    return () => {
      map.remove();
    };
  }, []);

  return <div ref={mapContainer} id="map" className="map" />;
}
