import React from 'react';

export default function Sidebar({ locations = [], onSelect }) {
  return (
    <aside className="sidebar panel">
      <header className="heading">
        <div className="badge">F</div>
        <div style={{ display: 'flex', flexDirection: 'column' }}>
          <h1 className="brand-title">Foodies</h1>
          <div className="title muted">Local favorites &amp; hidden gems</div>
        </div>
      </header>

      <div style={{ padding: '0.75rem 0' }}>
        <h2 id="panel-a">Explore locations</h2>
        <p className="desc">Browse curated spots and click any item to focus the map and open details.</p>
        <div className="content" style={{ marginBottom: '0.5rem' }}>
          <button className="btn">All</button>
          <button className="muted">Open now</button>
        </div>
      </div>

      <div id="listings" className="listings" style={{ overflowY: 'auto' }}>
        {locations.length === 0 && (
          <div style={{ padding: 12 }}>No locations available.</div>
        )}

        {locations.map((loc) => (
          <div
            key={loc.id}
            className="listing"
            style={{ padding: '10px 12px', borderBottom: '1px solid #eee', cursor: 'pointer' }}
            onClick={() => onSelect && onSelect(loc)}
          >
            <strong>{loc.title}</strong>
            <div style={{ fontSize: 13, color: '#666' }}>{loc.description}</div>
          </div>
        ))}
      </div>
    </aside>
  );
}
