import React, { useRef } from 'react';
import foodies from '../data/foodies_parsed';

const EXTRA_FIELDS = [
  { aliases: ['saturatedFat_g', 'saturated_fat_g', 'saturated_fat', 'satFat_g'], label: 'Saturated Fat (g)' },
  { aliases: ['transFat_g', 'trans_fat_g', 'trans_fat'], label: 'Trans Fat (g)' },
  { aliases: ['cholesterol_mg', 'cholesterol'], label: 'Cholesterol (mg)' },
  { aliases: ['dietaryFiber_g', 'dietary_fiber_g', 'fiber_g', 'dietary_fiber'], label: 'Dietary Fiber (g)' },
  { aliases: ['sugars_g', 'sugars', 'sugar_g'], label: 'Sugars (g)' },
  { aliases: ['addedSugars_g', 'added_sugars_g', 'added_sugars'], label: 'Added Sugars (g)' },
  { aliases: ['potassium_mg', 'potassium'], label: 'Potassium (mg)' },
  { aliases: ['vitaminA_percent', 'vitamin_a_pct', 'vitamin_a', 'vitaminA'], label: 'Vitamin A (%DV)' },
  { aliases: ['vitaminC_percent', 'vitamin_c_pct', 'vitamin_c', 'vitaminC'], label: 'Vitamin C (%DV)' },
  { aliases: ['vitaminD_percent', 'vitamin_d_pct', 'vitamin_d', 'vitaminD'], label: 'Vitamin D (%DV)' },
  { aliases: ['calcium_mg', 'calcium'], label: 'Calcium (mg)' },
];

function getFirst(item, aliases) {
  for (const a of aliases) {
    if (Object.prototype.hasOwnProperty.call(item, a) && item[a] != null) return item[a];
    const lowerKey = Object.keys(item).find(k => k.toLowerCase() === a.toLowerCase());
    if (lowerKey && item[lowerKey] != null) return item[lowerKey];
  }
  return null;
}

function NutritionTable({ items, horizontal = false, containerRef = null }) {
  if (!items || items.length === 0) return null;

  const presentExtras = EXTRA_FIELDS.filter(f => items.some(it => getFirst(it, f.aliases) != null));

  const gridContainer = { display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(320px, 1fr))', gap: 12 };
  const scrollContainer = { display: 'flex', gap: 12, overflowX: 'auto', paddingBottom: 8, scrollSnapType: 'x mandatory' };
  const cardBase = { border: '1px solid #e6e6e6', borderRadius: 8, padding: 12, background: '#fff', boxShadow: '0 1px 0 rgba(0,0,0,0.02)' };
  const card = horizontal ? { ...cardBase, flex: '0 0 320px', scrollSnapAlign: 'start' } : cardBase;
  const title = { fontSize: 15, marginBottom: 8 };
  const row = { display: 'flex', justifyContent: 'space-between', padding: '4px 0', fontSize: 13 };
  const labelStyle = { color: '#444' };
  const valueStyle = { color: '#111', fontWeight: 600 };

  const containerStyle = horizontal ? scrollContainer : gridContainer;

  return (
    <div ref={containerRef} style={containerStyle}>
      {items.map((it, idx) => (
        <div key={idx} style={card}>
          <div style={title}>{it.item || '—'}</div>

          <div style={{display: 'flex', gap: 12}}>
            <div style={{flex: 1}}>
              <div style={row}><div style={labelStyle}>Calories</div><div style={valueStyle}>{it.calories ?? '—'}</div></div>
              <div style={row}><div style={labelStyle}>Total Fat (g)</div><div style={valueStyle}>{it.totalFat_g ?? '—'}</div></div>
              <div style={row}><div style={labelStyle}>Total Carbs (g)</div><div style={valueStyle}>{it.totalCarbs_g ?? '—'}</div></div>
              <div style={row}><div style={labelStyle}>Protein (g)</div><div style={valueStyle}>{it.protein_g ?? '—'}</div></div>
              <div style={row}><div style={labelStyle}>Sodium (mg)</div><div style={valueStyle}>{it.sodium_mg ?? '—'}</div></div>
            </div>

            <div style={{width: 1, background: '#f2f2f2'}} />

            <div style={{flex: 1}}>
              {presentExtras.map((e, j) => {
                const val = getFirst(it, e.aliases);
                return (
                  <div key={j} style={row}><div style={labelStyle}>{e.label}</div><div style={valueStyle}>{val != null ? String(val) : '—'}</div></div>
                );
              })}
            </div>
          </div>

          <div style={{marginTop: 8, fontSize: 13}}>{it.url ? <a href={it.url} target="_blank" rel="noreferrer">Source</a> : '—'}</div>
        </div>
      ))}
    </div>
  );
}

function NutritionTableView({ items }) {
  if (!items || items.length === 0) return null;

  const baseCols = [
    { key: 'item', label: 'Item', get: i => i.item || '—' },
    { key: 'calories', label: 'Calories', get: i => i.calories ?? '—' },
    { key: 'totalFat_g', label: 'Total Fat (g)', get: i => i.totalFat_g ?? '—' },
    { key: 'totalCarbs_g', label: 'Total Carbs (g)', get: i => i.totalCarbs_g ?? '—' },
    { key: 'protein_g', label: 'Protein (g)', get: i => i.protein_g ?? '—' },
    { key: 'sodium_mg', label: 'Sodium (mg)', get: i => i.sodium_mg ?? '—' },
  ];

  const presentExtras = EXTRA_FIELDS.filter(f => items.some(it => getFirst(it, f.aliases) != null));

  const tableStyle = { width: '100%', borderCollapse: 'collapse', marginBottom: 18 };
  const thStyle = { textAlign: 'left', borderBottom: '1px solid #ddd', padding: '8px', fontSize: 13, background: '#fafafa' };
  const tdStyle = { padding: '8px', borderBottom: '1px solid #f2f2f2', fontSize: 13 };

  // wrap table so it can be a horizontal scroll item
  const wrapper = { minWidth: 760, flex: '0 0 760px', scrollSnapAlign: 'start' };

  return (
    <div style={wrapper}>
      <table style={tableStyle}>
      <thead>
        <tr>
          {baseCols.map(c => <th key={c.key} style={thStyle}>{c.label}</th>)}
          {presentExtras.map((e, idx) => <th key={'ex-'+idx} style={thStyle}>{e.label}</th>)}
          <th style={thStyle}>Source</th>
        </tr>
      </thead>
      <tbody>
        {items.map((it, idx) => (
          <tr key={idx}>
            {baseCols.map(c => <td key={c.key} style={tdStyle}>{String(c.get(it))}</td>)}
            {presentExtras.map((e, j) => {
              const val = getFirst(it, e.aliases);
              return <td key={'val-'+j} style={tdStyle}>{val != null ? String(val) : '—'}</td>;
            })}
            <td style={tdStyle}>{it.url ? <a href={it.url} target="_blank" rel="noreferrer">link</a> : '—'}</td>
          </tr>
        ))}
      </tbody>
      </table>
    </div>
  );
}

function CountryRow({ brand, country, items, useTable, countryHeaderStyle }) {
  const scrollerRef = useRef(null);

  const scrollByPage = (dir = 1) => {
    const el = scrollerRef.current;
    if (!el) return;
    const amount = el.clientWidth || 600;
    el.scrollBy({ left: dir * amount, behavior: 'smooth' });
  };

  const btn = { border: '1px solid #ddd', background: '#fff', borderRadius: 6, padding: '6px 8px', cursor: 'pointer' };
  const scrollerWrapper = { display: 'flex', alignItems: 'center', gap: 8 };
  const scrollerInner = { display: 'flex', gap: 12, overflowX: 'auto', paddingBottom: 8, scrollSnapType: 'x mandatory' };

  return (
    <div style={{marginBottom: 18}}>
      <div style={countryHeaderStyle}><strong>{country}</strong></div>
      <div style={scrollerWrapper}>
        <button aria-label="scroll-left" style={btn} onClick={() => scrollByPage(-1)}>◀</button>
        <div style={{flex: 1}}>
          {useTable ? (
            <div ref={scrollerRef} style={scrollerInner}>
              <NutritionTableView items={items} />
            </div>
          ) : (
            <NutritionTable items={items} horizontal={true} containerRef={scrollerRef} />
          )}
        </div>
        <button aria-label="scroll-right" style={btn} onClick={() => scrollByPage(1)}>▶</button>
      </div>
    </div>
  );
}

export default function WhatsInYourFood() {
  // Build grouping from parsed CSV data: restaurant -> country -> items
  const grouped = foodies.reduce((acc, item) => {
    const brand = item.restaurant || 'Unknown';
    const country = item.country || 'Unknown';
    if (!acc[brand]) acc[brand] = {};
    if (!acc[brand][country]) acc[brand][country] = [];
    acc[brand][country].push(item);
    return acc;
  }, {});

  const containerStyle = { padding: 24, maxWidth: 1000, margin: '0 auto' };
  const brandStyle = { marginTop: 20, marginBottom: 8 };
  const countryHeader = { fontSize: 16, margin: '12px 0 6px' };

  return (
    <div style={containerStyle}>
      <h1>Decode Your Dish — Nutritional Breakdown</h1>
      <p>For each chain below, each country's items are shown in a table — extra nutrient columns appear only if present in the data.</p>

      {Object.keys(grouped).map(brand => (
        <section key={brand}>
          <h2 style={brandStyle}>{brand}</h2>
          {(() => {
            // case-insensitive matching for KFC table countries (include common variants)
            const kfcTableSet = new Set(['uk','united kingdom','gb','great britain','india','mexico','mx']);
            return Object.keys(grouped[brand]).map(country => {
              const countryKey = (country || '').toString().trim().toLowerCase();
              const useTable = brand === 'KFC' && kfcTableSet.has(countryKey);
              return (
                <CountryRow
                  key={brand+"-"+country}
                  brand={brand}
                  country={country}
                  items={grouped[brand][country]}
                  useTable={useTable}
                  countryHeaderStyle={countryHeader}
                />
              );
            });
          })()}
        </section>
      ))}

      <p style={{marginTop: 24}}><a href="/">Back to app</a></p>
    </div>
  );
}
