import React, { useRef, useEffect } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// (No dynamic loaders needed — Leaflet is imported from npm)

export default function Map({ locations = [], selectedLocation = null }) {
  const mapContainer = useRef(null);
  const mapRef = useRef(null);
  const markersRef = useRef([]);

    useEffect(() => {
      let cancelled = false;
      let tries = 0;

      const initLeaflet = () => {
        if (cancelled) return;
        const container = mapContainer.current;
        if (!container) {
          if (tries++ < 20) return setTimeout(initLeaflet, 100);
          // eslint-disable-next-line no-console
          console.warn('Leaflet: map container not found after retries');
          return;
        }

        const { clientWidth, clientHeight } = container;
        if (!clientWidth || !clientHeight) {
          if (tries++ < 20) return setTimeout(initLeaflet, 100);
          // eslint-disable-next-line no-console
          console.warn('Leaflet: map container has zero size after retries', clientWidth, clientHeight);
          // we'll proceed anyway
        }

        try {
          const map = L.map(container, { scrollWheelZoom: true }).setView([20, 0], 2);
          L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
            maxZoom: 19,
            attribution: '&copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors',
          }).addTo(map);

          mapRef.current = map;

          try { L.control.scale({ position: 'bottomleft', imperial: false }).addTo(map); } catch (e) {}

          const ensureSize = () => { try { map.invalidateSize && map.invalidateSize(); } catch (e) {} };
          ensureSize();
          const _t1 = setTimeout(ensureSize, 100);
          const _t2 = setTimeout(ensureSize, 500);
          const _t3 = setTimeout(ensureSize, 1000);
          const _t4 = setTimeout(ensureSize, 2000);
          const _t5 = setTimeout(ensureSize, 4000);
          const onResize = () => ensureSize();
          window.addEventListener('resize', onResize);

          // eslint-disable-next-line no-console
          console.log('Leaflet initialized', { clientWidth, clientHeight });
          try { if (typeof window !== 'undefined') window.__LEAFLET_MAP__ = map; } catch(e){}

          return () => {
            try { window.removeEventListener('resize', onResize); } catch (e) {}
            clearTimeout(_t1); clearTimeout(_t2); clearTimeout(_t3); clearTimeout(_t4); clearTimeout(_t5);
            try { map.remove(); } catch (e) {}
            mapRef.current = null;
          };
        } catch (e) {
          // eslint-disable-next-line no-console
          console.warn('Leaflet init failed:', e);
        }
      };

      initLeaflet();

      return () => { cancelled = true; };
    }, []);

  // Update markers (Leaflet)
  useEffect(() => {
    const map = mapRef.current;
    if (!map) return;

    // remove existing marker layers
    markersRef.current.forEach((m) => { try { map.removeLayer(m); } catch(e) {} });
    markersRef.current = [];

    locations.forEach((loc) => {
      try {
        const m = L.circleMarker([loc.coordinates[1], loc.coordinates[0]], { radius: 6, color: '#2a9df4', fillOpacity: 0.9 }).addTo(map);
        m.bindPopup(`<strong>${loc.title}</strong><br/>${loc.description}`);
        markersRef.current.push(m);
      } catch (e) {
        // ignore
      }
    });

    // if we added any markers, fit the map to their bounds for a good initial view
    try {
      if (markersRef.current.length > 0) {
        const group = L.featureGroup(markersRef.current);
        map.fitBounds(group.getBounds().pad(0.25), { animate: true });
      } else {
        // no markers: show a world view
        map.setView([20, 0], 2);
      }
    } catch (e) { /* ignore fit errors */ }
  }, [locations]);

  // Handle selection: open popup and set view
  useEffect(() => {
    if (!selectedLocation) return;
    const map = mapRef.current;
    if (!map) return;
    const [lng, lat] = selectedLocation.coordinates;
    map.setView([lat, lng], 14);
    const matched = markersRef.current.find((m) => {
      try { const latlng = m.getLatLng(); return Math.abs(latlng.lng - lng) < 1e-6 && Math.abs(latlng.lat - lat) < 1e-6; } catch(e) { return false; }
    });
    if (matched && matched.openPopup) matched.openPopup();
  }, [selectedLocation]);

  return (
    <div style={{ position: 'relative', height: '100%' }}>
      <div ref={mapContainer} id="map" className="map" style={{ height: '100%' }} />

      {/* Map renders in the container above */}
    </div>
  );
}
