import React, { useState } from 'react';

export default function Sidebar({ locations = [], onSelect }) {
  const [active, setActive] = useState('all');

  // Official brand icons served via SimpleIcons CDN (SVG). We show the
  // white icon on a brand-coloured circular background for each button.
  const brands = [
    // Use white backgrounds for BK, Wendy's, KFC and McDonald's so their PNG art stands out.
    { id: 'mcd', label: "McDonald's", local: '/images/mcd.png', src: 'https://cdn.simpleicons.org/mcdonalds/ffffff', initial: 'McD', bg: '#ffffff' },
    { id: 'bk', label: 'Burger King', local: '/images/bk.png', src: 'https://cdn.simpleicons.org/burger-king/ffffff', initial: 'BK', bg: '#ffffff' },
    { id: 'wendys', label: "Wendy's", local: '/images/wendys.png', src: 'https://cdn.simpleicons.org/wendys/ffffff', initial: "W", bg: '#ffffff' },
    { id: 'kfc', label: 'KFC', local: '/images/kfc.png', src: 'https://cdn.simpleicons.org/kfc/ffffff', initial: 'KFC', bg: '#ffffff' },
  ];

  const handleBrandClick = (id) => {
    setActive(id);
    // notify parent of brand selection
    if (typeof onSelect === 'function') onSelect(id);
  };

  // Nutrition data for a serving of fries for each brand (sample values)
  const nutrition = {
    mcd: { name: "McDonald's Fries (Medium)", calories: 340, fat: '16g', carbs: '44g', protein: '4g', sodium: '230mg' },
    bk: { name: 'Burger King Fries (Medium)', calories: 380, fat: '18g', carbs: '48g', protein: '5g', sodium: '300mg' },
    wendys: { name: "Wendy's Fries (Medium)", calories: 420, fat: '19g', carbs: '54g', protein: '5g', sodium: '520mg' },
    kfc: { name: 'KFC Fries (Medium)', calories: 360, fat: '17g', carbs: '46g', protein: '4g', sodium: '410mg' },
  };

  // Per-country mock nutrition for McDonald's (randomized-ish demo values)
  const nutritionByCountry = {
    mcd: {
      'USA': { calories: 340, fat: '16g', carbs: '44g', protein: '4g', sodium: '230mg' },
      'UK': { calories: 320, fat: '15g', carbs: '42g', protein: '4g', sodium: '210mg' },
      'South Africa': { calories: 360, fat: '17g', carbs: '46g', protein: '4g', sodium: '260mg' },
      'India': { calories: 300, fat: '14g', carbs: '40g', protein: '3g', sodium: '200mg' },
      'Mexico': { calories: 380, fat: '18g', carbs: '48g', protein: '5g', sodium: '310mg' },
    }
  };

  // Small inline visualization component. Renders per-metric bars for each country.
  function NutritionViz({ byCountry }) {
    if (!byCountry) return null;
    const metrics = [
      { key: 'calories', label: 'Calories', unit: 'kcal', color: '#ff7f0e' },
      { key: 'fat', label: 'Fat', unit: 'g', color: '#2ca02c' },
      { key: 'carbs', label: 'Carbs', unit: 'g', color: '#1f77b4' },
      { key: 'protein', label: 'Protein', unit: 'g', color: '#9467bd' },
      { key: 'sodium', label: 'Sodium', unit: 'mg', color: '#d62728' },
    ];

    const countries = Object.keys(byCountry);

    const parseValue = (v) => {
      if (v == null) return 0;
      if (typeof v === 'number') return v;
      // strip non-digits (keep decimal if any)
      const cleaned = ('' + v).replace(/[^0-9.]/g, '');
      const n = parseFloat(cleaned);
      return Number.isFinite(n) ? n : 0;
    };

    return (
      <div className="nutrition-viz" aria-hidden>
        {metrics.map((m) => {
          const vals = countries.map((c) => parseValue(byCountry[c][m.key]));
          const max = Math.max(...vals, 1);
          return (
            <div className="metric-row" key={m.key}>
              <div className="metric-label">{m.label}</div>
              <div className="bars">
                {countries.map((country, i) => {
                  const v = vals[i];
                  const pct = Math.round((v / max) * 100);
                  return (
                    <div className="bar" key={country} title={`${country}: ${v} ${m.unit}`}>
                      <div className="bar-meta"><div className="bar-country">{country}</div><div className="bar-value">{v}{m.unit}</div></div>
                      <div className="bar-fill" style={{ width: `${pct}%`, background: m.color }} />
                    </div>
                  );
                })}
              </div>
            </div>
          );
        })}
      </div>
    );
  }

  return (
    <aside className="sidebar">
      <header className="heading" style={{ alignItems: 'flex-start', gap: 16 }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
          <h1 className="brand-title">
            <span className="brand-line1">The Balanced</span>
            <span className="brand-line2">Bite</span>
          </h1>
        </div>
      </header>

      <main style={{ paddingTop: 12 }}>
        <section style={{ display: 'flex', gap: 12, alignItems: 'center' }}>
          <span className="nutri-emoji" aria-hidden>üçé</span>
          <div>
            <h3 className="nutri-title" style={{ margin: 0 }}>Nutritional Info</h3>
            <div className="desc" style={{ color: '#666', marginTop: 6 }}>Your guide to fast food nutrition, everywhere</div>
          </div>
        </section>

        <div style={{ height: 18 }} />

        <section aria-label="Brand filters">
          <div className="brand-buttons">
            {brands.map((b) => (
              <button
                key={b.id}
                type="button"
                className={`brand-btn ${active === b.id ? 'active' : ''}`}
                onClick={() => handleBrandClick(b.id)}
                aria-pressed={active === b.id}
                title={b.label}
                style={{ background: b.bg }}
              >
                <img
                  className="brand-icon"
                  src={b.local || b.src}
                  alt={`${b.label} logo`}
                  onError={(e) => {
                    try {
                      if (!e || !e.target) return;
                      const img = e.target;

                      // If we tried the local .svg first, try the .png equivalent next
                      if (!img.dataset.triedLocalAlt && img.src && img.src.endsWith('.svg')) {
                        img.dataset.triedLocalAlt = '1';
                        const pngLocal = img.src.replace(/\.svg(\?.*)?$/, '.png');
                        img.src = pngLocal;
                        return;
                      }

                      // If we tried the local .png first and it failed, try the .svg equivalent
                      if (!img.dataset.triedLocalAlt && img.src && img.src.endsWith('.png')) {
                        img.dataset.triedLocalAlt = '1';
                        const svgLocal = img.src.replace(/\.png(\?.*)?$/, '.svg');
                        img.src = svgLocal;
                        return;
                      }

                      // If we haven't tried the CDN slug yet, try it now
                      if (!img.dataset.triedCdn && b.src) {
                        img.dataset.triedCdn = '1';
                        img.src = b.src;
                        return;
                      }

                      // Already tried CDN and local alternatives: fall back to generated initials SVG
                      if (img.dataset.fallback) return; // already applied fallback
                      img.dataset.fallback = '1';
                      const text = b.initial || (b.label || '').split(' ').map(s => s[0]).join('').slice(0,3);
                      const svg = `<?xml version="1.0" encoding="UTF-8"?>\n<svg xmlns='http://www.w3.org/2000/svg' width='72' height='72' viewBox='0 0 72 72'>\n  <rect width='100%' height='100%' rx='12' fill='${b.bg || '#cccccc'}'/>\n  <text x='50%' y='54%' font-family='Arial, Helvetica, sans-serif' font-size='20' fill='#fff' text-anchor='middle' alignment-baseline='middle'>${text}</text>\n</svg>`;
                      const uri = 'data:image/svg+xml;utf8,' + encodeURIComponent(svg);
                      img.src = uri;
                    } catch (err) {
                      // swallow fallback errors
                    }
                  }}
                />
              </button>
            ))}
          </div>
        </section>
        <div style={{ height: 12 }} />

        {/* Receipt-like nutrition panel */}
        <section aria-label="Nutrition receipt">
          <div className="receipt">
            <div className="receipt-sheet">
              {!active && (
                <div>
                  <div className="rcpt-store">FOODIES & CO.</div>
                  <div className="rcpt-title">Select a brand</div>
                  <div className="rcpt-line"><span>Click a logo</span><strong>to view fries</strong></div>
                </div>
              )}

              {active && (
                (() => {
                  // Special case: show per-country breakdown for McDonald's
                  if (active === 'mcd') {
                    const byCountry = (nutritionByCountry && nutritionByCountry.mcd) || {};
                    // Build a compact receipt grid with countries as columns
                    const countries = Object.keys(byCountry);
                    const metrics = [
                      { key: 'calories', label: 'Calories', unit: 'kcal' },
                      { key: 'fat', label: 'Fat', unit: 'g' },
                      { key: 'carbs', label: 'Carbs', unit: 'g' },
                      { key: 'protein', label: 'Protein', unit: 'g' },
                      { key: 'sodium', label: 'Sodium', unit: 'mg' },
                    ];

                    const parseVal = (v) => {
                      if (v == null) return '';
                      if (typeof v === 'number') return v;
                      return ('' + v).trim();
                    };

                    return (
                      <div>
                        <div className="rcpt-store">FOODIES & CO.</div>
                        <div className="rcpt-title">McDonald's Fries ‚Äî Medium (by country)</div>

                        <div className="rcpt-grid" role="table">
                          <div className="rcpt-grid-row rcpt-grid-header">
                            <div className="rcpt-grid-cell rcpt-metric">Metric</div>
                            {countries.map((c) => (
                              <div className="rcpt-grid-cell rcpt-country" key={c}>{c}</div>
                            ))}
                          </div>

                          {metrics.map((m) => (
                            <div className="rcpt-grid-row" key={m.key}>
                              <div className="rcpt-grid-cell rcpt-metric">{m.label}</div>
                              {countries.map((c) => (
                                <div className="rcpt-grid-cell rcpt-country" key={c + m.key}>
                                  {(() => {
                                    const v = byCountry[c] && byCountry[c][m.key];
                                    if (v == null) return '‚Äî';
                                    // Numeric calories shown with unit; leave other strings as-is
                                    if (m.key === 'calories' && typeof v === 'number') return `${v} ${m.unit}`;
                                    return parseVal(v) + (m.key === 'calories' ? ` ${m.unit}` : '');
                                  })()}
                                </div>
                              ))}
                            </div>
                          ))}
                        </div>

                        <div className="rcpt-note">* Values are approximate and randomized for demo purposes.</div>

                        {/* Horizontally-scrollable visualization cards */}
                        <div className="viz-row" role="list">
                          {(() => {
                            const metricDefs = [
                              { key: 'calories', label: 'Calories', unit: 'kcal', color: '#ff7f0e' },
                              { key: 'fat', label: 'Fat', unit: 'g', color: '#2ca02c' },
                              { key: 'carbs', label: 'Carbs', unit: 'g', color: '#1f77b4' },
                              { key: 'protein', label: 'Protein', unit: 'g', color: '#9467bd' },
                              { key: 'sodium', label: 'Sodium', unit: 'mg', color: '#d62728' },
                            ];

                            return metricDefs.map((m) => {
                              const vals = countries.map((c) => {
                                const raw = byCountry[c] && byCountry[c][m.key];
                                if (raw == null) return 0;
                                if (typeof raw === 'number') return raw;
                                const n = parseFloat(('' + raw).replace(/[^0-9.]/g, ''));
                                return Number.isFinite(n) ? n : 0;
                              });
                              const max = Math.max(...vals, 1);
                              return (
                                <div className="viz-card" key={m.key} role="listitem">
                                  <div className="viz-card-title">{m.label}</div>
                                  <div className="viz-card-chart">
                                    {countries.map((c, i) => {
                                      const v = vals[i];
                                      const pct = Math.round((v / max) * 100);
                                      return (
                                        <div className="card-bar" key={c} title={`${c}: ${v} ${m.unit}`}>
                                          <div className="card-bar-meta"><span className="bar-country">{c}</span><span className="bar-value">{v}{m.unit}</span></div>
                                          <div className="card-bar-fill" style={{ width: `${pct}%`, background: m.color }} />
                                        </div>
                                      );
                                    })}
                                  </div>
                                </div>
                              );
                            });
                          })()}
                        </div>
                      </div>
                    );
                  }

                  const data = nutrition[active];
                  if (!data) return <div className="receipt-empty">No nutrition data available.</div>;
                  return (
                    <div>
                      <div className="rcpt-store">FOODIES & CO.</div>
                      <div className="rcpt-title">Nutrition ‚Äî {data.name}</div>
                      <div className="rcpt-line"><span>Serving</span><strong>Medium</strong></div>
                      <div className="rcpt-line"><span>Calories</span><strong>{data.calories} kcal</strong></div>
                      <div className="rcpt-line"><span>Fat</span><strong>{data.fat}</strong></div>
                      <div className="rcpt-line"><span>Carbs</span><strong>{data.carbs}</strong></div>
                      <div className="rcpt-line"><span>Protein</span><strong>{data.protein}</strong></div>
                      <div className="rcpt-line"><span>Sodium</span><strong>{data.sodium}</strong></div>
                      <div className="rcpt-total">kcal / serving: <strong>{data.calories}</strong></div>
                      <div className="rcpt-note">* Values are approximate per serving.</div>
                    </div>
                  );
                })()
              )}
            </div>
          </div>
        </section>

        <div style={{ height: 8 }} />
      </main>
    </aside>
  );
}
