import React, { useRef, useEffect, useState } from 'react';
import mapboxgl from 'mapbox-gl';
import 'mapbox-gl/dist/mapbox-gl.css';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// (No dynamic loaders needed — Leaflet is imported from npm)

export default function Map({ locations = [], selectedLocation = null }) {
  const mapContainer = useRef(null);
  const mapRef = useRef(null);
  const markersRef = useRef([]);
  const [mapMode, setMapMode] = useState(null); // 'mapbox' | 'leaflet' | null

  useEffect(() => {
    const rawToken = process.env.REACT_APP_MAPBOX_TOKEN || window.__MAPBOX_TOKEN__;
    // Treat the placeholder string as "no token" so deployments that leave the
    // placeholder won't attempt Mapbox requests which fail with CORS/401.
    const token = rawToken && rawToken !== 'REPLACE_WITH_MAPBOX_TOKEN' ? rawToken : null;

    if (token) {
      // Initialize Mapbox
      mapboxgl.accessToken = token;
      const map = new mapboxgl.Map({
        container: mapContainer.current,
        style: 'mapbox://styles/mapbox/streets-v11',
        center: [-74.006, 40.7128],
        zoom: 12,
      });
      map.addControl(new mapboxgl.NavigationControl());
      mapRef.current = map;
      setMapMode('mapbox');

      map.on('load', () => {
        try {
          const canvas = map.getCanvas();
          if (canvas) canvas.style.background = '#e9f3ff';
        } catch (e) {
          // ignore
        }
      });

      return () => {
        map.remove();
        mapRef.current = null;
        setMapMode(null);
      };
    }

    // No token — use locally-installed Leaflet as fallback so a visible map appears
    try {
      const map = L.map(mapContainer.current, { scrollWheelZoom: true }).setView([20, 0], 2);
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        maxZoom: 19,
        attribution: '&copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors',
      }).addTo(map);

      mapRef.current = map;
      setMapMode('leaflet');

      // ensure proper sizing when inside flex containers
      setTimeout(() => map.invalidateSize && map.invalidateSize(), 200);
      const onResize = () => map.invalidateSize && map.invalidateSize();
      window.addEventListener('resize', onResize);

      return () => {
        try { window.removeEventListener('resize', onResize); } catch(e){}
        try{ map.remove(); }catch(e){}
        mapRef.current = null;
        setMapMode(null);
      };
    } catch (e) {
      // eslint-disable-next-line no-console
      console.warn('Leaflet fallback failed:', e);
      return () => { setMapMode(null); };
    }
  }, []);

  // Add markers for either Mapbox or Leaflet depending on mode
  useEffect(() => {
    if (mapMode === 'mapbox') {
      const map = mapRef.current;
      if (!map) return;
      // clear old
      markersRef.current.forEach((m) => m.remove());
      markersRef.current = [];

      locations.forEach((loc) => {
        const marker = new mapboxgl.Marker()
          .setLngLat(loc.coordinates)
          .setPopup(new mapboxgl.Popup({ offset: 12 }).setHTML(`<strong>${loc.title}</strong><p>${loc.description}</p>`))
          .addTo(map);
        markersRef.current.push(marker);
      });
    } else if (mapMode === 'leaflet') {
      const map = mapRef.current;
      if (!map) return;
      // remove existing layers created by markersRef
      markersRef.current.forEach((m) => { try { map.removeLayer(m); } catch (e) {} });
      markersRef.current = [];

      locations.forEach((loc) => {
        try {
          const m = L.circleMarker([loc.coordinates[1], loc.coordinates[0]], { radius: 6, color: '#2a9df4', fillOpacity: 0.9 }).addTo(map);
          m.bindPopup(`<strong>${loc.title}</strong><br/>${loc.description}`);
          markersRef.current.push(m);
        } catch (e) {
          // ignore
        }
      });
    }
  }, [locations, mapMode]);

  // Handle selection: flyTo / open popup in the active map
  useEffect(() => {
    if (!selectedLocation) return;
    if (mapMode === 'mapbox') {
      const map = mapRef.current;
      if (!map) return;
      const [lng, lat] = selectedLocation.coordinates;
      map.flyTo({ center: [lng, lat], zoom: 14 });
      const matched = markersRef.current.find((m) => {
        const lngLat = m.getLngLat();
        return Math.abs(lngLat.lng - lng) < 1e-6 && Math.abs(lngLat.lat - lat) < 1e-6;
      });
      if (matched) matched.togglePopup();
    } else if (mapMode === 'leaflet') {
      const map = mapRef.current;
      if (!map) return;
      const [lng, lat] = selectedLocation.coordinates;
      // eslint-disable-next-line no-undef
      map.setView([lat, lng], 14);
      const matched = markersRef.current.find((m) => {
        try {
          const latlng = m.getLatLng();
          return Math.abs(latlng.lng - lng) < 1e-6 && Math.abs(latlng.lat - lat) < 1e-6;
        } catch (e) { return false; }
      });
      if (matched && matched.openPopup) matched.openPopup();
    }
  }, [selectedLocation, mapMode]);

  return (
    <div style={{ position: 'relative', height: '100%' }}>
      <div ref={mapContainer} id="map" className="map" style={{ height: '100%' }} />
      {mapMode === null && (
        <div style={{ position: 'absolute', top: 80, left: 16, background: 'rgba(255,255,255,0.95)', padding: 10, borderRadius: 6, zIndex: 2 }}>
          <strong>Loading map...</strong>
          <div style={{ fontSize: 13, marginTop: 6 }}>Initializing map. If you prefer Mapbox, set a token.</div>
        </div>
      )}
    </div>
  );
}
